import React, { useState } from 'react'
import fetch from 'isomorphic-unfetch'

class Page extends React.Component {
  static async getInitialProps(ctx) {
    const callApi = [
      fetch('https://gateway.tuanndl.com/api/v1/article/' + ctx.query.id),
      fetch('https://gateway.tuanndl.com/api/v1/comment/' + ctx.query.id)
    ];

    let responses = await Promise.all(callApi);
    console.log(responses)
    // return {
    //   data: responses[0].json().result,
    //   comment: responses[0].json().result,
    // }
    return {
      responses
      }
  }

  render() {
    const { data, comment } = this.props;
    console.log(comment, data)
    return <div>Next stars:</div>
  }
}

export default Page
