import React, { useState } from 'react'
import fetch from 'isomorphic-unfetch'

class Page extends React.Component {
  static async getInitialProps(ctx) {
    const callApi = [
      await fetch('https://gateway.tuanndl.com/api/v1/article/' + ctx.query.id),
      await fetch('https://gateway.tuanndl.com/api/v1/comment/' + ctx.query.id)
    ];

    let responses = await Promise.all(callApi);

    return {
      data: responses[0].json().result,
      comment: responses[0].json().result,
    }
  }

  render() {
    const { content } = this.props;
    return <div>Next stars: {content.content}</div>
  }
}

export default Page
