const FontFaceObserver = require('fontfaceobserver')

const arrFont = [
  {
    link: 'https://fonts.googleapis.com/css?family=Space+Mono&display=swap',
    name: 'Space Mono'
  },
  {
    link: 'https://fonts.googleapis.com/css?family=Roboto:300,400,500,700,900',
    name: 'Roboto'
  },
  {
    link: 'https://fonts.googleapis.com/css?family=Space+Mono&display=swap',
    name: 'Space Mono'
  },

  ,
  'https://fonts.googleapis.com/css?family=IBM+Plex+Serif&display=swap'
];
const Fonts = () => {
  for (const item of arrFont) {
    const link = document.createElement('link');
    link.href = 'https://fonts.googleapis.com/css?family=Roboto:300,400,500,700,900';
    link.rel = 'stylesheet';

    document.head.appendChild(link);

    const roboto = new FontFaceObserver('Roboto');

    roboto.load()
  }
}

export default Fonts;
