    <Layout>
      a
    </Layout>
  );
};

Page.getInitialProps = async function(ctx) {
  const callApi = [
    fetch('https://gateway.tuanndl.com/api/v1/article/' + ctx.query.id),
    fetch('https://gateway.tuanndl.com/api/v1/comment/' + ctx.query.id)
  ];

  let responses = await Promise.all(callApi);
  return {
    dataArticle: await responses[0].json(),
    dataComment: await responses[1].json(),
  }
};

export default Page
