import React, { useState } from 'react'
import fetch from 'isomorphic-unfetch'

class Page extends React.Component {
  static async getInitialProps(ctx) {
    const callApi = [
      await fetch('https://gateway.tuanndl.com/api/v1/article/' + ctx.query.id),
      await fetch('https://gateway.tuanndl.com/api/v1/comment/' + ctx.query.id)
    ];

    let content = await Promise.all(callApi);
    content = await content.json();
    return { data: json.result }
  }

  render() {
    const { data } = this.props;
    return <div>Next stars: {data.content}</div>
  }
}

export default Page
